import React, { useState } from "react";
import "./Contact.css";

function Contact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [emptyFields, setEmptyFields] = useState(["name, email, message"])

  const handleChange = (e) => {
    if (e.target.name === "name") {
      setName(e.target.value);
      if(e.target.value === "") {
        setEmptyFields(emptyFields.filter((f)=> f ))
      }
    } else if (e.target.name === "email") {
      setEmail(e.target.value);
      if(e.target.value === "") {
        alert("this field must be filled in")
      }
    } else if (e.target.name === "message") {
      setMessage(e.target.value);
      if(e.target.value === "") {
        alert("this field must be filled in")
      }
    }
  };
// const handleNotification= () =>{
//   if(!name)
// }
  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your submit logic here
  };
  return (
    <div className="form-content">
      <div className="form" onSubmit={handleSubmit}>
        <h1>Contact</h1>
        <div className="sub-form1">
          <label>Name:</label>
          <input
            type="text"
            placeholder=" "
            value={name}
            name="name"
            className="name-input1"
            onChange={handleChange}
          ></input>
        </div>
        <div className="sub-form2">
          <label>Email Address:</label>
          <input
            type="text"
            placeholder=" "
            value={email}
            name="email"
            className="name-input2"
            onChange={handleChange}
          ></input>
        </div>
        <div className="sub-form2">
          <label>Message:</label>
          <textarea
            type="text"
            placeholder=" "
            value={message}
            name="message"
            className="name-input3"
            onChange={handleChange}
          ></textarea>
        </div>
        <div>
          <button type="submit" id="sbm-btn">
            Submit
          </button>
        </div>
      </div>
    </div>
  );
}

export default Contact;
